#[test]
fn parse_rule() {
    use crate::Parseable;

    let buf = RuleBuffer::new_checked(&BYTES[..]).unwrap();
    let parsed: RuleMessage = buf.parse().unwrap();
    let expected = RuleMessage {
        flags: RuleFlags::FilterExit,
        action: RuleAction::Always,
        fields: vec![
            (
                RuleField::Watch("/etc/passwd".into()),
                RuleFieldFlags::Equal,
            ),
            (RuleField::Perm(15), RuleFieldFlags::Equal),
        ],
        syscalls: RuleSyscalls::new_maxed(),
    };
    assert_eq!(parsed, expected);
}

#[test]
fn emit_rule() {
    use crate::Emitable;
    let rule = RuleMessage {
        flags: RuleFlags::FilterExit,
        action: RuleAction::Always,
        fields: vec![
            (
                RuleField::Watch("/etc/passwd".into()),
                RuleFieldFlags::Equal,
            ),
            (RuleField::Perm(15), RuleFieldFlags::Equal),
        ],
        syscalls: RuleSyscalls::new_maxed(),
    };
    assert_eq!(rule.buffer_len(), BYTES.len());
    let mut buf = vec![0; BYTES.len()];
    rule.emit(&mut buf[..]);
    assert_eq!(&buf[..], &BYTES[..]);
}
